const InputForm = ({ onSimulate }) => {
  const [formData, setFormData] = useState({
    team: "",
    model: "",
    power: "",
    weight: "",
    downforce: "mediu",
    circuit: "Silverstone",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSimulate(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      <label>Echipă:</label>
      <input className="form-input" name="team" placeholder="Echipă" onChange={handleChange} required />

      <label>Model:</label>
      <input className="form-input" name="model" placeholder="Model" onChange={handleChange} required />

      <label>Putere Motor (CP):</label>
      <input className="form-input" name="power" type="number" placeholder="Putere" onChange={handleChange} required />

      <label>Greutate (kg):</label>
      <input className="form-input" name="weight" type="number" placeholder="Greutate" onChange={handleChange} required />

      <label>Downforce:</label>
      <select className="form-select" name="downforce" onChange={handleChange}>
        <option value="scăzut">Scăzut</option>
        <option value="mediu">Mediu</option>
        <option value="ridicat">Ridicat</option>
      </select>

      <label>Circuit:</label>
      <select className="form-select" name="circuit" onChange={handleChange}>
        <option value="Silverstone">Silverstone</option>
        <option value="Monaco">Monaco</option>
      </select>

      <button type="submit" className="button-primary">Simulează Performanța</button>
    </form>
  );
};

export default InputForm;
