import React, { useState } from "react";
import InputForm from "./components/InputForm";
const [formData, setFormData] = useState(null);

const App = () => {
  const [results, setResults] = useState(null);

  const handleSimulate = async (formData) => {
    setFormData(formData); // SalveazƒÉ datele utilizatorului
    const response = await fetch("/api/simulation", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });
    const data = await response.json();
    setResults(data);
  };
  
  

  const handleGeneratePDF = async () => {
    if (!formData || !results) {
      alert("Trebuie sƒÉ completezi simularea √Ænainte de a genera PDF-ul!");
      return;
    }
  
    const payload = {
      ...results, // Include rezultatele simulƒÉrii
      ...formData, // Include datele selectate de utilizator
    };
  
    const response = await fetch("http://localhost:5000/api/generate-pdf", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
  
    // DescarcƒÉ fi»ôierul
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", "Formula1_Report.pdf");
    document.body.appendChild(link);
    link.click();
    link.remove();
  };
  
  
  

  return (
    <div>
      <header>
        <img src="/images/F1-logo.png" alt="Formula 1 Logo" style={{ height: "40px", marginRight: "10px" }} />
        Formula 1 Simulation üèéÔ∏è
      </header>
      <div className="f1-container">
        <section className="description">
          <h2>Bun venit la Simularea Performan»õei Formula 1</h2>
          <p>
            AceastƒÉ aplica»õie vƒÉ permite sƒÉ evalua»õi performan»õa unui monopost pe diferite circuite,
            √Æn func»õie de parametrii specifici: puterea motorului, greutatea, nivelul de downforce »ôi circuitul ales.
          </p>
          <p>Introduce»õi datele √Æn formularul de mai jos »ôi descoperi»õi timpul pe tur »ôi viteza maximƒÉ simulatƒÉ!</p>
        </section>

        <InputForm onSimulate={handleSimulate} />

        {results && (
          <div className="result-card">
            <h2>Rezultate Simulare</h2>
            <p><strong>Timp pe Tur:</strong> {results.lapTime} secunde</p>
            <p><strong>VitezƒÉ MaximƒÉ:</strong> {results.maxSpeed} km/h</p>
            <button className="button-primary" onClick={handleGeneratePDF}>
              DescarcƒÉ Raportul PDF
            </button>
          </div>
          
        )}
      </div>
      <footer>
        ¬© 2025 Formula 1 Simulation. Toate drepturile rezervate.
      </footer>
    </div>
  );
};

export default App;
