import React, { useState } from "react";
import "./InfoPage.css";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

const InfoPage = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const tireData = [
    { type: "Soft", description: "Pneuri rapide, cu aderență ridicată, dar uzură rapidă.", image: "/images/soft.png" },
    { type: "Medium", description: "Un echilibru între viteză și durabilitate.", image: "/images/medium.png" },
    { type: "Hard", description: "Rezistente pentru strategii lungi, cu aderență mai scăzută.", image: "/images/hard.png" },
    { type: "Inters", description: "Pneuri pentru condiții de ploaie ușoară.", image: "/images/inters.png" },
    { type: "Wet", description: "Pneuri pentru ploaie abundentă și aderență maximă pe apă.", image: "/images/wet.png" },
  ];

  const circuitData = [
    { name: "Silverstone", description: "Un circuit clasic cu viraje rapide și o istorie bogată.", image: "/images/silverstone.png" },
    { name: "Monaco", description: "Un circuit stradal cu viraje strânse și provocări tehnice.", image: "/images/monaco.png" },
    { name: "Monza", description: "Cel mai rapid circuit din Formula 1, cunoscut drept Templul Vitezei.", image: "/images/monza.png" },
  ];

  const handleSlideChange = (nextSlide) => {
    setCurrentSlide(nextSlide); // Setăm direct slide-ul curent
  };

  return (
    <div className="info-container">
      <h1>Informații</h1>

      {/* Pneuri */}
      <section className="tires-section">
        <h2 className="tires-title">Pneuri</h2>
        <Carousel
          additionalTransfrom={0}
          arrows
          autoPlaySpeed={3000}
          centerMode={false}
          className="carousel"
          containerClass="carousel-container"
          draggable
          infinite
          keyBoardControl
          minimumTouchDrag={80}
          responsive={{
            desktop: { breakpoint: { max: 3000, min: 1024 }, items: 1 },
            tablet: { breakpoint: { max: 1024, min: 464 }, items: 1 },
            mobile: { breakpoint: { max: 464, min: 0 }, items: 1 },
          }}
          showDots={true}
          slidesToSlide={1}
          swipeable
          afterChange={(current) => handleSlideChange(current)} // Asigurăm actualizarea corectă
        >
          {tireData.map((tire, index) => (
            <div key={index} className="tire-card">
              <img src={tire.image} alt={tire.type} />
            </div>
          ))}
        </Carousel>
        <div className="carousel-text-container">
          <p className="carousel-text">
            {tireData[currentSlide]?.description || "Descriere indisponibilă"}
          </p>
        </div>
      </section>


      {/* Downforce */}
      <section className="downforce-section">
        <h2>Downforce</h2>
        <div className="downforce-content">
          <p>
            Downforce-ul influențează semnificativ aderența monoposturilor. Nivelul ridicat de downforce oferă o
            stabilitate mai bună în viraje, dar reduce viteza maximă.
          </p>
          <img src="/images/downforce.jpg" alt="Downforce" />
        </div>
      </section>

      {/* Condiții Meteo */}
      <section className="weather-section">
        <h2>Condiții Meteo</h2>
        <div className="weather-content">
          <div className="weather-card">
            <img src="/images/sunny.g" alt="Soare" />
            <p>Performanță optimă în condiții uscate și însorite.</p>
          </div>
          <div className="weather-card">
            <img src="/images/rain.jpg" alt="Ploaie" />
            <p>Pneuri speciale și viteze mai scăzute în condiții de ploaie.</p>
          </div>
        </div>
      </section>

      {/* Circuite */}
      <section className="circuits-section">
        <h2>Circuite</h2>
        <p>
          Circuitele din Formula 1 variază de la piste rapide până la circuite tehnice și stradale.
          Fiecare circuit prezintă provocări unice pentru echipe și piloți.
        </p>
        <Carousel
          additionalTransfrom={0}
          arrows
          autoPlaySpeed={3000}
          className="carousel"
          containerClass="carousel-container"
          draggable
          infinite
          keyBoardControl
          renderDotsOutside={false}
          responsive={{
            desktop: { breakpoint: { max: 3000, min: 1024 }, items: 1 },
            tablet: { breakpoint: { max: 1024, min: 464 }, items: 1 },
            mobile: { breakpoint: { max: 464, min: 0 }, items: 1 },
          }}
          showDots={true}
          slidesToSlide={1}
        >
          {circuitData.map((circuit, index) => (
            <div key={index} className="circuit-card">
              <img src={circuit.image} alt={circuit.name} />
              <div className="carousel-text">{circuit.description}</div>
            </div>
          ))}
        </Carousel>
      </section>

    </div>
  );
};

export default InfoPage;
